qFieldDefs:
=if(
	//fonds de rente Milan et GA39_2   
	CD_SOUS_TYPE_SUPPORT = 'TGPRODBFR' 	OR CD_PRODUIT_CODA = 'GA392' 
	// Contrats avec garantie VUT et taux de la garantie > 1
	OR (left(if(aggr(nodistinct count(distinct {1<TYPOPS={'VU','DC'}>}TYPOPS), NUM_CONTRAT)>0, aggr(nodistinct only({1<TYPOPS={'VU','DC'}>} TYPGDC), NUM_CONTRAT)),3) ='VUT' 
		AND if(aggr(nodistinct count(distinct {1<TYPOPS={'VU','DC'}>}TYPOPS), NUM_CONTRAT)>0, aggr(nodistinct only({1<TYPOPS={'VU','DC'}>} TAUTUN), NUM_CONTRAT), 0) >1)
	//Contrat GA60_2 avec garantie VUTM 
	OR (if(aggr(nodistinct count(distinct {1<TYPOPS={'VU','DC'}>}TYPOPS), NUM_CONTRAT)>0, aggr(nodistinct only({1<TYPOPS={'VU','DC'}>} TYPGDC), NUM_CONTRAT))='VUTM' 
		AND CD_PRODUIT_CODA = 'GA602')
	// Contrats hors précédents avec garantie VU mais pas VUT 
	OR (if(aggr(nodistinct count(distinct {1<TYPOPS={'VU','DC'}>}TYPOPS), NUM_CONTRAT)>0, -1, 0)
		AND left(if(aggr(nodistinct count(distinct {1<TYPOPS={'VU','DC'}>}TYPOPS), NUM_CONTRAT)>0, aggr(nodistinct only({1<TYPOPS={'VU','DC'}>} TYPGDC), NUM_CONTRAT)),3)<>'VUT')

	,'IFRS4ASS' 

	,if( // Contrats avec garantie non signigficative ou sans garantie mais investissement possible en TG
		FG_PROPOSE = 'OUI'	
		,'IFRS4PB'		
		, if((CD_TYPE_SUPPORT_1 = 'FG' and MT_PROVISION_CPT>0) 
			// si le produit ne permet pas d’investir sur du FG mais qu’il existe une PM FG non nulle + tautun > 1%)
			,if( aggr(nodistinct sum({<D_BILAN={'$(=Date(Max(D_BILAN)))'}, CD_TYPE_SUPPORT_1={'FG'}>}MT_PROVISION_CPT), NUM_CONTRAT) 
				AND if(aggr(nodistinct count(distinct {1<TYPOPS={'VU','DC'}>}TYPOPS), NUM_CONTRAT)>0, aggr(nodistinct only({1<TYPOPS={'VU','DC'}>} TAUTUN), NUM_CONTRAT), 0) >1
				, 'IFRS4ASS'
				, 'IFRS4PB'
			)
			,'IAS39'
		)
	)
)
