#sheet-title.sheet-grid { 
	color: #ffffff;
	padding-bottom: 5px;
	font-family: Roboto, sans serif;
}

.sheet-title-container {
	background:#FFFFFF !important; 
	background:-moz-linear-gradient(left, #381A0A 0%, #FFFFFF 100%)!important; 
	background:-webkit-linear-gradient(left, #381A0A 0%, #FFFFFF 100%)!important; 
	background: -o-linear-gradient(left, #381A0A 0%, #FFFFFF 100%)!important; 
	background:-ms-linear-gradient(left, #381A0A 0%, #FFFFFF 100%)!important;  
	background:linear-gradient(to right, #381A0A 0%, #FFFFFF 100%)!important; 
	border-bottom: 1px solid rgba(0,0,0,0.1)!important; 
}

.sheet-title-text {
	background-image: url("/content/Default/Logo_LMEP_New.png");
	background-position: right;
	background-repeat: no-repeat;
	background-size: contain;
}

#sheet-title > div > div > button.MuiButtonBase-root.MuiButton-root.MuiButton-text.MuiButton-textInherit {
	color: #381A0A !important;
}

.qv-object-kpi{
	border: 2px solid gray !important;
	border-radius: 10px;
}

.sheet-title-container {
	position: fixed !important;
	top: 89px;
	left: 0;
	width: 100%;
	height: 80px;
	z-index: 999 !important;
}

.qv-panel {
	position: fixed !important;
	top: 129px;
	left: 0;
	height: Calc(100vh - 129px);
}

.app-details-content {
	z-index: 2000 !important;
	background-color: inherit;
}

